import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "../services/api";
import "../styles/home.css";

const EditBlog = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [form, setForm] = useState({ title: "", content: "", image: "", category: "" });

  useEffect(() => {
    const fetchBlog = async () => {
      const res = await axios.get(`/api/blogs/${id}`);
      setForm(res.data.blog);
    };
    fetchBlog();
  }, [id]);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.put(`/api/blogs/${id}`, form);
    navigate("/dashboard");
  };

  return (
    <div className="container py-5">
      <h2 className="fw-bold mb-4">‚úèÔ∏è Edit Blog</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Title</label>
          <input className="form-control" name="title" value={form.title} onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label>Category</label>
          <input className="form-control" name="category" value={form.category} onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label>Image URL</label>
          <input className="form-control" name="image" value={form.image} onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label>Content</label>
          <textarea className="form-control" name="content" value={form.content} onChange={handleChange} rows="6" required />
        </div>
        <button className="btn custom-btn-primary" type="submit">
          üöÄ Update Blog
        </button>
      </form>
    </div>
  );
};

export default EditBlog;
