import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "../services/api";
import "../styles/home.css";

const BlogDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [blog, setBlog] = useState(null);
  const [comment, setComment] = useState("");
  const [userId, setUserId] = useState("");
  const [loading, setLoading] = useState(true);

  // Fetch blog by ID
  useEffect(() => {
    const fetchBlog = async () => {
      try {
        const res = await axios.get(`/blogs/${id}`);
        setBlog(res.data.blog);
        setUserId(res.data.userId);
        setLoading(false);
      } catch (error) {
        console.error("Error loading blog:", error);
        setLoading(false);
      }
    };
    fetchBlog();
  }, [id]);

  const handlePostComment = async () => {
    if (!comment.trim()) return;
    try {
      const res = await axios.post(`/blogs/${id}/comments`, { text: comment });
      setBlog((prev) => ({
        ...prev,
        comments: [...prev.comments, res.data],
      }));
      setComment(""); // Reset the input
    } catch (error) {
      console.error("Failed to post comment:", error);
    }
  };

  const handleEdit = () => navigate(`/edit/${id}`);
  const handleBack = () => navigate("/dashboard");

  if (loading) return <p className="text-center mt-5">Loading...</p>;
  if (!blog) return <p className="text-center mt-5">Blog not found</p>;

  return (
    <div className="container py-5">
      <button className="btn btn-link mb-3" onClick={handleBack}>
        ‚Üê Back to Blogs
      </button>

      <img
        src={blog.image}
        alt={blog.title}
        className="img-fluid rounded mb-4"
        style={{ borderRadius: "16px", maxHeight: "400px", objectFit: "cover" }}
      />

      <span className="badge custom-badge-primary mb-2">{blog.category}</span>
      <h2 className="fw-bold">{blog.title}</h2>
      <p className="text-muted">{blog.content}</p>

      {userId === blog.author?._id && (
        <button className="btn custom-btn-primary mb-4" onClick={handleEdit}>
          ‚úèÔ∏è Edit Blog
        </button>
      )}

      <hr />
      <h4 className="fw-bold">Comments</h4>

      {blog.comments?.length === 0 ? (
        <p className="text-muted">No comments yet. Be the first to comment!</p>
      ) : (
        blog.comments.map((c, idx) => (
          <div key={idx} className="mb-2 p-2 bg-light rounded">
            <strong>{c.name || "User"}</strong>: {c.text}
          </div>
        ))
      )}

      <div className="mt-3">
        <textarea
          className="form-control mb-2"
          rows={3}
          placeholder="Write a comment..."
          value={comment}
          onChange={(e) => {
            const val = e.target.value;
            if (typeof val === "string") setComment(val);
          }}
        />
        <button className="btn btn-purple" onClick={handlePostComment}>
          üöÄ Post Comment
        </button>
      </div>
    </div>
  );
};

export default BlogDetail;
