import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../services/api";
import "../styles/home.css";

const CreateBlog = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    title: "",
    content: "",
    image: "",
    category: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("/api/blogs", form);
      navigate("/dashboard");
    } catch (err) {
      alert("Blog creation failed: " + (err.response?.data?.message || err.message));
    }
  };

  return (
    <div className="container py-5">
      <h2 className="fw-bold mb-4">‚úçÔ∏è Create New Blog</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Title</label>
          <input
            className="form-control"
            name="title"
            value={form.title}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label>Category</label>
          <input
            className="form-control"
            name="category"
            value={form.category}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label>Image URL</label>
          <input
            className="form-control"
            name="image"
            value={form.image}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label>Content</label>
          <textarea
            className="form-control"
            name="content"
            value={form.content}
            onChange={handleChange}
            rows="6"
            required
          ></textarea>
        </div>
        <button className="btn custom-btn-primary" type="submit">
          üöÄ Publish Blog
        </button>
      </form>
    </div>
  );
};

export default CreateBlog;
