import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../services/api";
import BlogCard from "../components/BlogCard";
import { FaArrowLeft } from "react-icons/fa";
import "../styles/profile.css";

const Profile = () => {
  const [user, setUser] = useState(null);
  const [blogs, setBlogs] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get("/auth/profile");
        setUser(res.data.user);
        setBlogs(res.data.blogs);
      } catch (err) {
        console.error("Profile fetch error:", err);
      }
    };
    fetchProfile();
  }, []);

  return (
    <div className="container py-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <button className="btn btn-outline-primary" onClick={() => navigate("/dashboard")}>
          <FaArrowLeft className="me-2" />
          Back to Blogs
        </button>
        <button className="btn custom-btn-primary" onClick={() => navigate("/create")}>
          âž• Create New Blog
        </button>
      </div>

      <h2 className="fw-bold mb-3">My Profile</h2>
      <p className="text-muted mb-4">Manage your blog posts and account settings</p>

      <div className="card p-4 mb-5 shadow-sm rounded-4 border-0 bg-white">
        <h5 className="fw-semibold">Account Information</h5>
        <p><strong>Name:</strong> {user?.name || "N/A"}</p>
        <p><strong>Email:</strong> {user?.email || "N/A"}</p>
        <p><strong>Member since:</strong> {user?.createdAt ? new Date(user.createdAt).toLocaleString('default', { month: 'long', year: 'numeric' }) : "N/A"}</p>
      </div>

      <h4 className="fw-bold mb-3">My Blog Posts</h4>
      {blogs.length === 0 ? (
        <p className="text-muted">You haven't written any blogs yet.</p>
      ) : (
        <div className="row g-4">
          {blogs.map((blog) => (
            <div className="col-md-4" key={blog._id}>
              <BlogCard blog={blog} />
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Profile;
