import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../services/api";
import BlogCard from "../components/BlogCard";
import "../styles/home.css";

const Profile = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState({});
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get("/api/auth/profile");
        setUser(res.data.user);
        setBlogs(res.data.blogs);
      } catch (err) {
        console.error("Failed to fetch profile:", err);
      }
    };
    fetchProfile();
  }, []);

  return (
    <div className="container py-5">
      <div className="d-flex justify-content-between mb-4">
        <button className="btn btn-outline-secondary" onClick={() => navigate("/dashboard")}>
          ‚Üê Back to Blogs
        </button>
        <button className="btn custom-btn-primary" onClick={() => navigate("/create")}>
          ‚ûï Create New Blog
        </button>
      </div>

      <h2 className="fw-bold mb-3">üë§ My Profile</h2>
      <p className="text-muted">Manage your blog posts and account settings</p>

      <div className="bg-white p-4 rounded shadow-sm mb-5">
        <h5 className="fw-bold">Account Information</h5>
        <p><strong>Name:</strong> {user.name || "N/A"}</p>
        <p><strong>Email:</strong> {user.email || "N/A"}</p>
        <p><strong>Member since:</strong> {new Date(user.createdAt).toLocaleString()}</p>
      </div>

      <h4 className="fw-bold mb-3">üìö My Blog Posts</h4>
      {blogs.length === 0 ? (
        <p>You haven't written any blogs yet.</p>
      ) : (
        <div className="row g-4">
          {blogs.map((blog) => (
            <div className="col-md-4" key={blog._id}>
              <BlogCard blog={blog} />
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Profile;
