import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../services/api";
import BlogCard from "../components/BlogCard";
import "../styles/home.css";

const Profile = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState({});
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get("/api/auth/profile");
        setUser(res.data.user);
        setBlogs(res.data.blogs);
      } catch (err) {
        console.error("Failed to fetch profile:", err);
      }
    };
    fetchProfile();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this blog?")) return;
    try {
      await axios.delete(`/api/blogs/${id}`);
      setBlogs((prev) => prev.filter((blog) => blog._id !== id));
    } catch (err) {
      console.error("Delete error:", err);
    }
  };

  return (
    <div className="container py-5">
      <h2 className="fw-bold mb-3">üë§ My Profile</h2>
      <p><strong>Name:</strong> {user.name}</p>
      <p><strong>Email:</strong> {user.email}</p>
      <hr />
      <h4 className="fw-bold mt-4">üìù My Blogs</h4>
      <div className="row g-4 mt-2">
        {blogs.length === 0 ? (
          <p>You haven't written any blogs yet.</p>
        ) : (
          blogs.map((blog) => (
            <div className="col-md-4" key={blog._id}>
              <BlogCard blog={blog} />
              <div className="d-flex justify-content-between mt-3">
                <button className="btn btn-sm btn-outline-primary" onClick={() => navigate(`/edit/${blog._id}`)}>‚úèÔ∏è Edit</button>
                <button className="btn btn-sm btn-outline-danger" onClick={() => handleDelete(blog._id)}>üóë Delete</button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Profile;
