import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "../services/api";
import "../styles/home.css";

const EditBlog = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [form, setForm] = useState({
    title: "",
    content: "",
    category: "",
    image: "",
  });
  const [file, setFile] = useState(null);

  useEffect(() => {
    const fetchBlog = async () => {
      try {
        const res = await axios.get(`/blogs/${id}`);
        setForm(res.data.blog);
      } catch (error) {
        console.error("Error fetching blog:", error);
        alert("Failed to load blog");
      }
    };
    fetchBlog();
  }, [id]);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleFileChange = (e) => {
    const selected = e.target.files[0];
    if (selected) setFile(selected);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append("title", form.title);
      formData.append("category", form.category);
      formData.append("content", form.content);
      if (file) {
        formData.append("image", file);
      } else {
        formData.append("image", form.image);
      }

      const res = await axios.put(`/blogs/${id}`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert(res.data.message || "Blog updated successfully");
      navigate("/dashboard");
    } catch (err) {
      console.error("Update failed:", err);
      alert(err.response?.data?.message || "Update failed");
    }
  };

  const handleDelete = async () => {
    const confirm = window.confirm("Are you sure you want to delete this blog?");
    if (!confirm) return;
    try {
      const res = await axios.delete(`/blogs/${id}`);
      alert(res.data.message || "Blog deleted successfully");
      navigate("/dashboard");
    } catch (err) {
      console.error("Delete failed:", err);
      alert(err.response?.data?.message || "Delete failed");
    }
  };

  return (
    <div className="container py-5" style={{ maxWidth: "800px" }}>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="fw-bold">Edit Blog Post</h2>
        <button className="btn btn-danger" onClick={handleDelete}>
          🗑️ Delete Blog
        </button>
      </div>

      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div className="mb-3">
          <label className="form-label">Title</label>
          <input
            className="form-control"
            name="title"
            value={form.title}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Category</label>
          <input
            className="form-control"
            name="category"
            value={form.category}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Upload New Image</label>
          <input
            className="form-control"
            type="file"
            accept="image/*"
            onChange={handleFileChange}
          />
          {form.image && !file && (
            <div className="mt-2">
              <img
                src={`http://localhost:5000${form.image}`}
                alt="Current"
                style={{ maxHeight: "150px", borderRadius: "12px" }}
              />
              <p className="text-muted small">Current Image</p>
            </div>
          )}
        </div>

        <div className="mb-4">
          <label className="form-label">Content</label>
          <textarea
            className="form-control"
            name="content"
            value={form.content}
            onChange={handleChange}
            rows={6}
            required
          />
        </div>

        <div className="d-flex justify-content-end gap-3">
          <button className="btn btn-light" type="button" onClick={() => navigate("/dashboard")}>
            Cancel
          </button>
          <button className="btn btn-primary" type="submit">
            Save Changes
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditBlog;
