import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../services/api";
import "../styles/profile.css";

const Profile = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [blogs, setBlogs] = useState([]);

  const fetchProfile = async () => {
    try {
      const res = await axios.get("/auth/profile");
      setUser(res.data.user);
      setBlogs(res.data.blogs);
    } catch (err) {
      console.error("Failed to fetch profile", err);
    }
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  return (
    <div className="container py-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <button className="btn btn-link text-decoration-none" onClick={() => navigate("/dashboard")}>
          ⬅️ Back to Blogs
        </button>
        <button className="btn custom-btn-primary" onClick={() => navigate("/create")}>
          ➕ Create New Blog
        </button>
      </div>

      <h2 className="fw-bold">My Profile</h2>
      <p className="text-muted mb-4">Manage your blog posts and account settings</p>

      <div className="bg-white rounded-4 shadow-sm p-4 mb-5">
        <h5 className="fw-bold mb-3">Account Information</h5>
        <p><strong>Name:</strong> {user?.name || "Loading..."}</p>
        <p><strong>Email:</strong> {user?.email || "Loading..."}</p>
        <p><strong>Member since:</strong> {user ? new Date(user.createdAt).toLocaleDateString() : "Loading..."}</p>
      </div>

      <h4 className="fw-bold mb-3">My Blog Posts</h4>
      {blogs.length === 0 ? (
        <p className="text-muted">You haven't written any blogs yet.</p>
      ) : (
        <div className="row g-4">
          {blogs.map((blog) => (
            <div className="col-md-4" key={blog._id}>
              <div className="card border-0 shadow-sm rounded-4 h-100">
                <img src={blog.image} alt={blog.title} className="card-img-top rounded-top-4" />
                <div className="card-body">
                  <span className="badge bg-light text-primary mb-2">{blog.category}</span>
                  <h5 className="card-title">{blog.title}</h5>
                  <p className="card-text text-muted">{blog.content.slice(0, 100)}...</p>
                  <p className="text-muted small">🕒 5 min read</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Profile;
