import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "../styles/auth.css";
import { loginUser, registerUser } from "../services/api";

const AuthPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
  });
  const [error, setError] = useState("");

  useEffect(() => {
    const mode = new URLSearchParams(location.search).get("mode") === "register" ? false : true;
    setIsLogin(mode);
  }, [location.search]);

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        const res = await loginUser({ email: formData.email, password: formData.password });
        localStorage.setItem("token", res.token);
        navigate("/dashboard");
      } else {
        if (formData.password !== formData.confirmPassword) {
          return setError("Passwords do not match");
        }
        const res = await registerUser({ name: formData.name, email: formData.email, password: formData.password });
        localStorage.setItem("token", res.token);
        navigate("/dashboard");
      }
    } catch (err) {
      setError(err.response?.data?.message || "Something went wrong");
    }
  };

  return (
    <div className="auth-page">
      <div className="auth-container">
        <div className="auth-box">
          <h2 className="auth-title">{isLogin ? "Login" : "Register"}</h2>
          <p className="auth-subtitle">Educational Blog</p>
          <div className="auth-toggle">
            <button className={`toggle-btn ${isLogin ? "active" : ""}`} onClick={() => navigate("/login?mode=login")}>
              Login
            </button>
            <button className={`toggle-btn ${!isLogin ? "active" : ""}`} onClick={() => navigate("/login?mode=register")}>
              Register
            </button>
          </div>
          <form className="auth-form" onSubmit={handleSubmit}>
            {!isLogin && (
              <>
                <label>Name</label>
                <input
                  type="text"
                  name="name"
                  placeholder="Enter your name"
                  className="form-control"
                  value={formData.name}
                  onChange={handleChange}
                  required
                />
              </>
            )}
            <label>Email</label>
            <input
              type="email"
              name="email"
              placeholder="Enter your email"
              className="form-control"
              value={formData.email}
              onChange={handleChange}
              required
            />
            <label>Password</label>
            <input
              type="password"
              name="password"
              placeholder={isLogin ? "Enter your password" : "Choose a password"}
              className="form-control"
              value={formData.password}
              onChange={handleChange}
              required
            />
            {!isLogin && (
              <>
                <label>Confirm Password</label>
                <input
                  type="password"
                  name="confirmPassword"
                  placeholder="Confirm your password"
                  className="form-control"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  required
                />
              </>
            )}
            {error && <p style={{ color: "red" }}>{error}</p>}
            <button type="submit" className="auth-btn">
              {isLogin ? "Login" : "Create Account"}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
