import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../services/api";
import BlogCard from "../components/BlogCard";
import "../styles/home.css";

const BlogDashboard = () => {
  const [blogs, setBlogs] = useState([]);
  const [search, setSearch] = useState("");
  const navigate = useNavigate();

  const fetchBlogs = async () => {
    try {
      const res = await axios.get("/api/blogs");
      setBlogs(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchBlogs();
  }, []);

  const filteredBlogs = blogs.filter(
    (blog) =>
      blog.title.toLowerCase().includes(search.toLowerCase()) ||
      blog.author.name.toLowerCase().includes(search.toLowerCase())
  );

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div className="container py-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <input
          type="text"
          className="form-control w-50"
          placeholder="üîç Search blogs..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <div>
          <button className="btn btn-link me-2" onClick={() => navigate("/profile")}>
            üë§ Profile
          </button>
          <button className="btn btn-link me-2" onClick={handleLogout}>
            üîÅ Sign Out
          </button>
          <button className="btn custom-btn-primary" onClick={() => navigate("/create")}>
            Create New Blog
          </button>
        </div>
      </div>
      <h2 className="mb-4 fw-bold">Educational Blog</h2>
      <div className="row g-4">
        {filteredBlogs.map((blog) => (
          <div className="col-md-4" key={blog._id}>
            <BlogCard blog={blog} />
          </div>
        ))}
        {filteredBlogs.length === 0 && <p>No blogs found.</p>}
      </div>
    </div>
  );
};

export default BlogDashboard;
