import React from "react";
import { useNavigate } from "react-router-dom";
import "../styles/home.css";

const BlogCard = ({ blog }) => {
  const navigate = useNavigate();

  return (
    <div
      className="rounded-4 shadow-sm bg-white h-100"
      style={{ cursor: "pointer", padding: "1rem" }}
      onClick={() => navigate(`/blogs/${blog._id}`)}
    >
      {/* Image */}
      <div
        style={{
          borderRadius: "12px",
          overflow: "hidden",
          height: "180px",
          marginBottom: "1rem",
        }}
      >
        <img
          src={`http://localhost:5000${blog.image}`} // ✅ FIXED: prefix with backend URL
          alt={blog.title}
          className="w-100 h-100"
          style={{ objectFit: "cover" }}
        />
      </div>

      {/* Category badge */}
      <span
        className="badge"
        style={{
          backgroundColor: "#f1e6ff",
          color: "#a259ff",
          fontSize: "0.75rem",
          padding: "0.3rem 0.6rem",
          borderRadius: "1rem",
        }}
      >
        {blog.category}
      </span>

      {/* Title */}
      <h5 className="fw-bold mt-2 mb-1">{blog.title}</h5>

      {/* Content preview */}
      <p className="text-muted mb-1" style={{ fontSize: "0.9rem" }}>
        {blog.content.slice(0, 100)}...
      </p>

      {/* Author */}
      <p className="text-muted small mb-0">
        By: {blog.author?.name || "Anonymous"} {/* ✅ FIXED: uses populated name */}
      </p>
    </div>
  );
};

export default BlogCard;
