import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../services/api";
import { FaArrowLeft, FaClock } from "react-icons/fa";
import "../styles/profile.css";

const Profile = () => {
  const [user, setUser] = useState({});
  const [blogs, setBlogs] = useState([]);
  const navigate = useNavigate();

  const fetchProfile = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("/auth/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUser(res.data.user);
      setBlogs(res.data.blogs);
    } catch (err) {
      console.error("Error fetching profile:", err);
    }
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  return (
    <div className="container py-5">
      <button
        className="btn btn-link mb-4"
        onClick={() => navigate("/dashboard")}
      >
        <FaArrowLeft /> Back to Blogs
      </button>

      <h2 className="fw-bold mb-3">My Profile</h2>
      <p className="text-muted mb-4">Manage your blog posts and account settings</p>

      {/* User Info */}
      <div className="card p-4 mb-5 shadow-sm">
        <h4 className="fw-bold mb-3">Account Information</h4>
        <p><strong>Name:</strong> {user.name}</p>
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Member since:</strong> {new Date(user.createdAt).toLocaleDateString()}</p>
      </div>

      {/* Blog Posts */}
      <h4 className="fw-bold mb-3">My Blog Posts</h4>
      {blogs.length === 0 ? (
        <p>You haven’t written any blogs yet.</p>
      ) : (
        <div className="row g-4">
          {blogs.map((blog) => (
            <div className="col-md-4" key={blog._id}>
              <div className="card h-100 p-3 shadow-sm">
                <img
                  src={`http://localhost:5000${blog.image}`} // ✅ FIXED image URL
                  alt={blog.title}
                  className="card-img-top mb-3"
                  style={{ height: "180px", objectFit: "cover", borderRadius: "12px" }}
                />
                <span className="badge bg-light text-primary mb-2">{blog.category}</span>
                <h5 className="fw-bold">{blog.title}</h5>
                <p className="text-muted mb-2" style={{ fontSize: "0.9rem" }}>
                  {blog.content.slice(0, 100)}...
                </p>
                <p className="text-muted small mb-0">
                  <FaClock className="me-1" />
                  5 min read
                </p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Profile;
